section.codeblock:
/* 
 * Reset all relevant counters
 */
*[class ~= "topic/topic"][is-chapter]:not([is-part]) {
    counter-reset: page 1 section1 0 figcount 0 tablecount 0 !important;
} 

/* 
 * Get rid of the chapter number from the titles in the TOC and Content.
 * Keep only sub-sections counters. 
 */

/* 
 * TOC
 */
*[class ~= "map/map"][*|numbering='deep']  *[class ~= "map/topicref"][is-chapter]:not([is-part]) > *[class ~= "map/topicmeta"]:before {
    content: "" !important;
}
*[class ~= "map/map"][*|numbering='deep']  *[class ~= "map/topicref"][is-chapter]:not([is-part]) > *[class ~= "map/topicref"] > *[class ~= "map/topicmeta"]:before {
    content: counter(toc-section1) ". " !important;
}
*[class ~= "map/map"][*|numbering='deep']  *[class ~= "map/topicref"][is-chapter]:not([is-part]) > *[class ~= "map/topicref"] > *[class ~= "map/topicref"] > *[class ~= "map/topicmeta"]:before {
    content: counter(toc-section1) "." counter(toc-section2) ". " !important;
}
*[class ~= "map/map"][*|numbering='deep']  *[class ~= "map/topicref"][is-chapter]:not([is-part]) > *[class ~= "map/topicref"] > *[class ~= "map/topicref"] > *[class ~= "map/topicref"] > *[class ~= "map/topicmeta"]:before {
    content: counter(toc-section1) "." counter(toc-section2) "." counter(toc-section3) ". " !important;
}

/* 
 * Content.
 */
*[class ~= "map/map"][*|numbering='deep'] *[class ~= "topic/topic"][is-chapter]:not([is-part]) > *[class ~= "topic/title"]:before{
        content: "" !important;
}
*[class ~= "map/map"][*|numbering='deep'] *[class ~= "topic/topic"][is-chapter]:not([is-part]) > *[class ~= "topic/topic"] > *[class ~= "topic/title"]:before{
        content: counter(section1) ". " !important;
}
*[class ~= "map/map"][*|numbering='deep'] *[class ~= "topic/topic"][is-chapter]:not([is-part]) > *[class ~= "topic/topic"] > *[class ~= "topic/topic"] > *[class ~= "topic/title"]:before{
        content: counter(section1) "." counter(section2) ". " !important;
}
*[class ~= "map/map"][*|numbering='deep'] *[class ~= "topic/topic"][is-chapter]:not([is-part]) > *[class ~= "topic/topic"] > *[class ~= "topic/topic"] > *[class ~= "topic/topic"] > *[class ~= "topic/title"]:before{
        content: counter(section1) "." counter(section2) "." counter(section3) ". " !important;
}

/* 
 * Reset all relevant counters
 */
*[class ~= "topic/topic"][is-chapter]:not([is-part]) {
    counter-reset: page 1 section1 0 figcount 0 tablecount 0 !important;
} 
/* 
 * Get rid of the chapter number from the titles in the TOC and Content.
 */

/* 
 * TOC
 */
*[class ~= "map/topicref"][is-chapter]:not([is-part]) > *[class ~= "map/topicmeta"] > *[class ~= "topic/navtitle"]:before{
    content:"" !important;
}

/* 
 * Content.
 */
*[class ~= "topic/topic"][is-chapter]:not([is-part]) > *[class ~= "topic/title"]:before {
    content: "";
}
